import React from "react";
import {connect} from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {createWidget, } from '../../../actions/widgetActions'
import WidgetListItem from './WidgetListItem';

const WidgetListComponent = ({widgets}) =>
    <div className="row">
      <div className="col container fluid wbdv-widget-info">
        <div className="row wbdv-margin-below">
          <div className="col wbdv-heading-widget">
            <a className="fa fa-times pt-1 pr-2"
               onClick="window.location.href='#';"/>
            <h4>List Widget</h4>
            <ul>
            {
              widgets.map(widget =>
                  <WidgetListItem key={widget._id} widget={widget}/>)
            }
            </ul>
          </div>
        </div>
      </div>
    </div>;

export default WidgetListComponent;
