import React from 'react'
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../CourseManager.css';

import CourseGridCardComponent from './CourseGridCardComponent'

class CourseGridComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      highlightedCourseId: null
    }
  }

  onHighlight(courseId) {
    this.setState({highlightedCourseId: courseId});
  }

  render () {
    return (
        <div className="container-fluid">
          <h3>Course Grid {this.props.courses.length}</h3>
          <div className="row row-cols-3 card-deck">
            {this.props.courses &&
              this.props.courses.map(course =>
                  <CourseGridCardComponent
                      key={course._id}
                      isSelected={course._id === this.state.highlightedCourseId}
                      onHighlight={this.onHighlight.bind(this)}
                      course={course}
                      onCourseTitleSelect={this.props.onCourseTitleSelect}
                      showCourseEditor={this.props.showCourseEditor}
                      deleteCourse={this.props.deleteCourse}
                  />
              )
            }
          </div>
        </div>
    )
  }
}
