class WidgetService {
    async createWidget (topicId, widget) {
        const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}/widgets`, {
            method: 'POST',
            body: JSON.stringify(widget),
            headers: {'content-type': 'application/json'}
        });
        return await response.json();
    }

    async findWidgetsForTopic (topicId) {
        const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}/widgets`);
        return await response.json();
    }

    updateWidget = async (widgetId, widget) => {
      const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/widgets/${widgetId}`, {
        method: 'PUT',
        body: JSON.stringify(widget),
        headers: {'content-type': 'applications/json'}
      });
      return await response.json();
    }

    deleteWidget = async (widgetId) => {
        const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/widgets/${widgetId}`, {
            method: 'DELETE '
        });
        return await response.json();
    }
  }

  export default WidgetService;
