class ModuleService {
  createModule = async (courseId, module) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/courses/${courseId}/modules`, {
      method: 'POST',
      body: JSON.stringify(module),
      headers: {'content-type': 'application/json'}
    });
    return await response.json();
  }

  findModulesForCourse = async (courseId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/courses/${courseId}/modules`);
    return await response.json();
  }

  async getModule(moduleId) {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/modules/${moduleId}`);
  }

  updateModule = async (moduleId, module) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/modules/${moduleId}`, {
      method: 'PUT',
      body: JSON.stringify(module),
      headers: {'content-type': 'applications/json'}
    });
    return await response.json();
  }

  deleteModule = async (moduleId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/modules/${moduleId}`, {
      method: 'DELETE'
    });
    return await response.json();
  }
}

export default ModuleService;
