import React from 'react'
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../CourseManager.css';

import CourseGridComponentStateful from './CourseGridComponentStateful'
import CourseGridCardComponent from './CourseGridCardComponent'
import CourseRowComponentStateful from "../row/CourseRowComponentStateful";

// const CourseGridComponent = ({editCourse, deleteCourse, updateFormState, courses}) =>
const CourseGridComponent =
    ({courses, showCourseEditor, hideCourseEditor, deleteCourse}) =>
    <div class="container-fluid">
      <h3>Course Grid {courses.length}</h3>
      <div className="row row-cols-3 card-deck">
        {
          courses.map(course =>
              <CourseGridCardComponent
                  key={course._id}
                  course={course}
                  showCourseEditor={showCourseEditor}
                  hideCourseEditor={hideCourseEditor}
                  deleteCourse={deleteCourse}
                  />
            )
          }
      </div>
    </div>;

export default CourseGridComponent
