export const CREATE_WIDGET = 'CREATE_WIDGET';
export const DELETE_WIDGET = 'DELETE_WIDGET';
export const SET_SELECTED_TOPIC = 'SET_SELECTED_TOPIC';
export const UPDATE_WIDGET = 'UPDATE_WIDGET';

export const createWidget = (widget, topicId) => ({
  title: 'New Widget',
  widgetType: widget.type,
  _id:(new Date()).getTime()+"",
  topicId: topicId,
  type: CREATE_WIDGET,
  newWidget: widget
  });

  export const deleteWidget = (widgetId) => ({
    type: DELETE_WIDGET,
    widgetId: widgetId
  });

  export const setSelectedTopic = (widgets) => ({
    type: SET_SELECTED_TOPIC,
    widgets: widgets
  });

  export const updateWidget = (widget) => ({
    title: widget.title,
    type: UPDATE_WIDGET,
    widget: widget
  });

  export default {CREATE_WIDGET, DELETE_WIDGET, SET_SELECTED_TOPIC, UPDATE_WIDGET,
    deleteWidget, createWidget, setSelectedTopic, updateWidget}
