import React from "react";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'

// const TopicPillItem = ({topic}) =>
//     <li className="nav-item wbdv-topic-pill-list">
//       <a className="nav-link" href="#">{topic.title}</a>
//     </li>;

class TopicPillItem extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      isEditing: false,
      title: props.topic.title
    };
  }

  onEdit() {
    this.setState({isEditing: true});
    this.props.onSelect();
  }

  onChange(event) {
    this.setState({title: event.target.value});
  }

  onSave() {
    const newTopic = {...this.props.topic, title: this.state.title};
    console.log('saving new lesson: ' + JSON.stringify(newTopic));
    this.props.onSave(newTopic);
    this.setState({isEditing: false});
  }
}

export default TopicPillItem;
