class TopicService {
  createTopic = (lessonId, topic) =>
      fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}/topics`, {
        method: 'POST',
        body: JSON.stringify(topic),
        headers: {
          'content-type': 'application/json'
        }
      }).then(response => response.json());

  findTopicsForLesson = (lessonId) =>
      fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}/topics`)
      .then(response => response.json());


  updateTopic = async (topicId, topic) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
      method: 'PUT',
      body: JSON.stringify(topic),
      headers: {
        'content-type': 'applications/json'
      }
    })
    return await response.json();
  }

  deleteTopic = (topicId) =>
      fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
        method: "DELETE"}).then(response => response.json());
}

export default TopicService;
