import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import ModuleService from '../../../services/ModuleService';

const service = new ModuleService();

class ModuleListItem extends React.Component {

  state = {
    isActive: false,
    isEditing: false,
    module: null,
  }

  constructor(props) {
    super(props);

    this.setState({
      isActive: props.isActive,
      isEditing: props.isEditing,
    });
  }

  componentDidMount() {
    this.getLatestModule(this.props.moduleId);
  }

  async getLatestModule(moduleId) {
    const module = await service.getModule(moduleId);
    this.setState({module: module});
  }



  setActive() {
    this.setState({isActive: true});
    this.props.clearOtherSelections(this.state.module._id);
  }

  setEditing() {
    this.setState({isEditing: true});
    this.props.clearOtherSelections(this.state.module._id);
  }

  delete() {
    this.props.deleteModule(this.state.module._id);
  }

  save() {
    service.updateModule(this.state.module._id, this.state.module);
  }

  render() {
    return (
      <li className={`list-group-item wbdv-module-item wbdv-module-item-title
        ${this.state.isActive ? 'active' : ''}`}
        onClick={() => this.setActive()} key={module._id}>

        {!this.state.isEditing &&
          <div>
            <span>{this.state.module.title}</span>
            <span className="float-right">
              <i className="fa fa-trash fa-2x"
                onClick={() => this.delete()}/>
              <i className="fa fa-pencil fa-2x"
                onClick={() => this.setEditing()}/>
            </span>
          </div>
        }
        {this.state.isEditing &&
          <div>
            <input type="text"
            className="typing-container" value={this.state.module.title} />

            <span className="float-right">
              <i className="fa fa-check fa-2x"
                onClick={() => this.saveModule()}/>
            </span>
          </div>
        }
      </li>
    );
  }
}

export default ModuleListItem;
