import React from "react";
import {connect} from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {createTopic, deleteTopic, setSelectedLesson, updateTopic} from '../../../actions/topicActions'
import TopicPillItem from "./TopicPillItem";
import TopicService from '../../../services/TopicService'

const topicService = new TopicService();

const TopicPillComponent = (topics) =>
  <p>{JSON.stringify(topics)}</p>;
      // <ul class="nav nav-pills wbdv-margin-below wbdv-topic-pill">
      //   {topics.map(topic =>
      //       <TopicPillItem key={topic._id} topic={topic}/>
      //   )}
      //   <a id="wbdv-topic-add-btn" className="fa fa-plus-circle pt-1 fa-2x"
      //      onClick="window.location.href = '#';">
      //   </a>
      // </ul>;

// export default TopicPillComponent;


const stateToPropertyMapper = (state) => {
  return {
      topics: state.topics.topics
  }
}

const dispatchToPropertyMapper = (dispatch) => {
  return {
      createTopic: (lessonId) =>
          topicService.createTopic(topicId, {title: 'New topic'})
          .then(actualTopic =>
              dispatch(createTopic(actualTopic))),

      setSelectedLesson: (lessonId) =>
          topicService.findtopicsForLesson(lessonId)
          .then(actualTopics =>
              dispatch(setSelectedLesson(actualTopics))),

      updateTopic: (topicId, topic) =>
          topicService.updateTopic(topicId, topic)
          .then(updatedTopic =>
              dispatch(updateTopic(updatedTopic))),

      deleteTopic: (topicId) =>
          topicService.deleteTopic(topicId)
          .then(status =>
              dispatch(deleteTopic(topicId)))
  }
}

export default connect(
  stateToPropertyMapper,
  dispatchToPropertyMapper)
(TopicPillComponent)
