import React from 'react';
import {connect} from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css';
import '../../CourseManager/CourseManager.css';
import {createLesson, deleteLesson, findAllLessonsForModule, updateLesson} from '../../../actions/lessonActions'
import LessonService from '../../../services/LessonService';
import LessonTabItem from './LessonTabItem';

// const LessonTabComponent = (lessons) =>
