//         <i className="fa fa-plus-circle pt-1 fa-2x"></i>
//       </button>
//     </div>;

// export default LessonTabComponent

const service = new LessonService();

class LessonTabComponent extends React.Component {
  state = {
    selectedLesson: null,
    editinglessonId: null,
    lessons: [],
  };

  componentDidMount() {
    this.updateLessons();
  }

  async updateLessons() {
    const lessons = await service.findLessonsForModule(this.props.moduleId);
    this.setState({lessons: lessons});
  }

  async addLesson() {
    await this.props.createLesson(this.props.moduleId);
    this.updateLessons;
  }

  onSelect(lessonId) {
    this.setState({selected: moduleId});
    this.props.history.push(`/course/${this.props.courseId}/module/${moduleId}`)
  }

  async onDelete(moduleId) {
    this.setState({selectedModuleId: moduleId});
    await this.props.deleteModule(moduleId);
    await this.updateModules();
  }

  async onSave(moduleId, newModule) {
    console.log(`Saving module ${moduleId} with content ${JSON.stringify(newModule)}`);
    await this.props.updateModule(moduleId, newModule);
    await this.updateModules();
  }

  // componentDidUpdate(prevProps, prevState, snapshot) {
  //   if(this.props.moduleId !== prevProps.moduleId) {
  //     this.updateLessons();
  //   }
  // }

//   updateLessonTitle(event) {
//     this.setState(prevState => ({
//       lesson: {
//         ...prevState.lesson,
//         title: event.target.value
//       }}))
//   }

  render() {
    // return (<div>{JSON.stringify(this.state.lessons)}</div>)
    return (
        <div>
        <div className="collapse navbar-collapse">
            <ul className="navbar-nav">
            {this.state.lessons &&   
                this.state.lessons.map(lesson =>
                <LessonTabItem key={lesson._id} lesson={lesson}/> ) }
            </ul>
        </div>
          <i className="fa fa-plus-circle pt-1 fa-3x"
            id="wbdv-add-module"
            onClick={() => this.props.createLesson(this.props.moduleId)}/>
        </div>
        );
  }
}

const stateToPropertyMapper = (state) => {
  return {
    lessons: state.lessons.lessons
  }
}

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createLesson: (moduleId) =>
        service.createLesson(moduleId, {title: 'New lesson'})
        .then(actuallesson =>
            dispatch(createLesson(actuallesson, moduleId))),

    findAllLessonsForModule: (moduleId) =>
        service.findLessonsForModule(moduleId)
        .then(actuallessons =>
            dispatch(findAllLessonsForModule(actuallessons))),

    updateLesson: (lessonId, lesson) =>
        service.updateLesson(lessonId, lesson)
        .then(updatedlesson =>
            dispatch(updateLesson(updatedlesson))),

    deleteLesson: (lessonId) =>
        service.deleteLesson(lessonId)
        .then(status =>
            dispatch(deleteLesson(lessonId)))
  }
};

export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper)
(LessonTabComponent)
