import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import HeadingWidgetComponent from './HeadingWidgetComponent';
import ParagraphWidgetComponent from './ParagraphWidgetComponent';

const HEADING = 'Heading';
const PARAGRAPH = 'Paragraph';

class WidgetListItemComponent extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      widget: props.widget,
      type: HEADING,
      index: props.index,
      name: ''
    }

    this.setState({widget: {...this.state.widget, index: this.state.index}});
  }

  componentDidMount() {

  }

  //TODO: should not update the server right away
  async onDeleteWidget() {
    await this.props.onDelete(this.state.widget._id)
  }

  changeType(type) {
    if (type === HEADING) {
      this.setState({type: PARAGRAPH})
    } else {
      this.setState({type: HEADING})
    }

    this.setState({widget: {...this.state.widget, widgetType:
            this.state.type.toUpperCase()}});

    //TODO: delete this when only using update all
    this.props.updateWidget(this.state.widget._id, this.state.widget);
  }

  updateName(event) {
    this.setState({name: event.target.value});
    // TODO: when you implement the server you will take a name
  }

  //TODO: add name to both components
  render() {
    return (
        <div className={"col container fluid wbdv-widget info"}>

          {this.state.type === HEADING &&
          <HeadingWidgetComponent
              type={HEADING}
              widgetId={this.state.widget._id}
              updateWidget={this.props.updateWidget} //TODO: delete later
              changeType={this.changeType.bind(this)}
              onDeleteWidget={this.onDeleteWidget.bind(this)}
              updateName={this.updateName.bind(this)}
              moveUp={this.props.moveUp}
              moveDown={this.props.moveDown}/>}

          {this.state.type === PARAGRAPH &&
          <ParagraphWidgetComponent
              type={PARAGRAPH}
              widgetId={this.state.widget._id}
              index={this.state.index}
              lastElem={this.props.lastElem}
              updateWidget={this.props.updateWidget} //TODO: delete later
              changeType={this.changeType.bind(this)}

              onDeleteWidget={this.onDeleteWidget.bind(this)}
              updateName={this.updateName.bind(this)}
              moveUp={this.props.moveUp}
              moveDown={this.props.moveDown}/>}

        </div>
    )
  }
}

export default WidgetListItemComponent;
