import React from "react";
import '../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../CourseManager/CourseManager.css'
import CourseGridComponent from "./grid/CourseGridComponent";
import CourseTableComponent from "./row/CourseTableComponent";
import CourseHeadingComponent from "./CourseHeadingComponent";
import CourseSearchAndToggleComponent from "./CourseSearchAndToggleComponent";

class CourseListComponent extends React.Component {

  constructor(props) {
    super(props);
  }

  onSelectCourse() {
    this.props.onCourseTitleSelect(this.state.course.title);
    this.props.showCourseEditor();
  }

  render() {
    return (
        <div className="container-fluid">
          <CourseHeadingComponent/>
          <CourseSearchAndToggleComponent
              updateFormState={this.props.updateFormState}
              addCourse={this.props.addCourse}
              toggle={this.props.toggle}/>
          {this.props.layout === 'table' &&
          <CourseTableComponent
              courses={this.props.courses}
              onCourseTitleSelect={this.props.onCourseTitleSelect}
              showCourseEditor={this.props.showCourseEditor}
              deleteCourse={this.props.deleteCourse}/>}
          {this.props.layout === 'grid' &&
          <CourseGridComponent
              courses={this.props.courses}
              onCourseTitleSelect={this.props.onCourseTitleSelect}
              showCourseEditor={this.props.showCourseEditor}
              deleteCourse={this.props.deleteCourse}/>}
        </div>
    )
  }
}

