import React from "react";
import {connect} from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {
  createWidget,
  deleteWidget,
  findAllWidgetsForTopic,
  updateWidget,
  saveAllWidgets,
  moveWidgetUp,
  moveWidgetDown
} from '../../../actions/widgetActions'
import WidgetListItemComponent from './WidgetListItemComponent';
import SaveAndPreviewComponent from './SaveAndPreviewComponent';
import WidgetService from '../../../services/WidgetService'

const service = new WidgetService();
const HEADING = 'Heading';
const PARAGRAPH = 'Paragraph';

class WidgetListComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      widgets: [],
    };

    this.updateWidgets();
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if(prevProps.topicId !== this.props.topicId) {
      this.updateWidgets();
    }
  }

  async onSaveAll() {
    await this.props.saveAllWidgets(this.props.topicId, this.state.widgets);
    this.updateWidgets();
  }

  getLastElement() {
    return this.state.widgets.length - 1;
  }

  async updateWidgets() {
    const widgets = await service.findWidgetsForTopic(this.props.topicId);
    this.setState({widgets: widgets});
  }

  async addWidget() {
    // this.props.createWidget(this.props.topicId);
    this.setState({widgets: [...this.state.widgets,
        {title: 'New widget', widgetType: 'Heading', id: (new Date()).getTime()+""}]})
  }

  onDelete(widgetId) {
    let newWidgets = this.state.widgets.filter(widget => widget.id !== widgetId);
    const widgetState = this.props.deleteWidget(widgetId, newWidgets);
    this.setState({widgets: widgetState.widgets});
  }

  swapElements(widgets, x, y) {
    let temp = widgets[x];
    widgets[x] = widgets[y];
    widgets[y] = temp;
    widgets[x].index = x;
    widgets[y].index = y;
    return widgets;
  }

  async moveUp(widget) {
    let index = this.state.widgets.indexOf(widget);
    this.setState(
        {widgets: this.swapElements(this.state.widgets, index, index - 1)});
    this.props.moveWidgetUp(widget, this.state.widgets);
  }

  async moveDown(widget) {
    let index = this.state.widgets.indexOf(widget);
    this.setState(
        {widgets: this.swapElements(this.state.widgets, index, index + 1)});
    this.props.moveWidgetDown(widget, this.state.widgets);
  }

  updateWidgetType(widgetId, widget, type) {
    // let type = event.target.value;
    // if (type === this.state.type) return;
    let newType = (widget.widgetType === HEADING) ? PARAGRAPH : HEADING;
    let newWidget = {...widget, widgetType: newType};
    newWidget.topicId = this.props.topicId;
    this.setState({widgets: this.state.widgets.map(curWidget =>
        curWidget.id === widgetId ? newWidget : curWidget)});
    this.props.updateWidget(widgetId, widget, this.state.widgets);
  }

  updateWidgetName(widgetId, widget, event) {
    console.log(event.target.value);
    let newWidget = {...widget, name: event.target.value};

    newWidget.topicId = this.props.topicId;

    this.setState({widgets: this.state.widgets.map(curWidget =>
          curWidget.id === widgetId ? newWidget : curWidget)});

    this.props.updateWidget(widgetId, newWidget, this.state.widgets);
  }

  updateWidgetContent(widgetId, widget, event) {
    let newWidget = {...widget, content: event.target.value};
    console.log(newWidget.content);
    newWidget.topicId = this.props.topicId;
    this.setState({widgets: this.state.widgets.map(curWidget =>
          curWidget.id === widgetId ? newWidget : curWidget)});
    this.props.updateWidget(widgetId, newWidget, this.state.widgets);
  }

  updateHeadingSize(widgetId, widget, event) {
    console.log(event.target.value);
    let newWidget = {...widget, headingSize: event.target.value};
    newWidget.topicId = this.props.topicId;
    this.setState({widgets: this.state.widgets.map(curWidget =>
          curWidget.id === widgetId ? newWidget : curWidget)});

    this.props.updateWidget(widgetId, widget, this.state.widgets);
  }

  render() {
    return (
        <span>
        <SaveAndPreviewComponent
            saveAll={this.onSaveAll.bind(this)}/>
        <div>
          {this.state.widgets && this.state.widgets.map((widget, index) =>
              <div>
                <WidgetListItemComponent
                    key={widget.id}
                    getLastElement={this.getLastElement.bind(this)}

                    widgetType={widget.widgetType === '' ?
                        HEADING : widget.widgetType}
                    name={widget.name}
                    content={widget.content}
                    headingSize={widget.headingSize}

                    index={index}
                    widget={widget}
                    moveUp={this.moveUp.bind(this, widget)}
                    moveDown={this.moveDown.bind(this, widget)}
                    onDelete={this.onDelete.bind(this, widget.id)}
                    updateWidgetType={this.updateWidgetType.bind(this, widget.id, widget)}
                    updateWidgetName={this.updateWidgetName.bind(this, widget.id, widget)}
                    updateWidgetContent={this.updateWidgetContent.bind(this, widget.id, widget)}
                    updateHeadingSize={this.updateHeadingSize.bind(this, widget.id, widget)}
                />
              </div>)
          }
          <div className="nav-item" onClick={() => this.addWidget()}>
            <i className="nav-link fa fa-plus-circle fa-2x"/>
          </div>
        </div>
      </span>
    )
  }
}

const stateToPropertyMapper = (state) => {
  return {
    widgets: state.widgets.widgets
  }
};

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createWidget: (topicId) =>
      dispatch(createWidget(topicId)),

    findAllWidgetsForTopic: (topicId) =>
        service.findWidgetsForTopic(topicId)
        .then(actualWidgets =>
            dispatch(findAllWidgetsForTopic(actualWidgets))),

    updateWidget: (widgetId, widget, widgets) =>
        dispatch(updateWidget(widgetId, widget, widgets)),
    // updateWidget: (widgetId, widget) =>
    //     service.updateWidget(widgetId, widget)
    //     .then(updatedWidget =>
    //         dispatch(updateWidget(updatedWidget))),

    deleteWidget: (widgetId, widgets) =>
        dispatch(deleteWidget(widgetId, widgets)),

    saveAllWidgets: (topicId, widgets) =>
        service.saveAllWidgets(topicId, widgets)
        .then(returnedWidgets =>
            dispatch(saveAllWidgets(returnedWidgets))),

    moveWidgetUp: (widget) =>
        dispatch(moveWidgetUp(widget)),

    moveWidgetDown: (widget) =>
        dispatch(moveWidgetDown(widget))
  }
};

export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper)
(WidgetListComponent)
