import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import HeadingWidgetComponent from './HeadingWidgetComponent';
import ParagraphWidgetComponent from './ParagraphWidgetComponent';

const HEADING = 'Heading';
const PARAGRAPH = 'Paragraph';

class WidgetListItemComponent extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      widget: props.widget,
      widgetType: props.widgetType,
      index: props.index,
      name: ''
    };

    this.state.widget.index = props.index;
  }

  // TODO: properly implement later
  // changeType(event) {
  //   // let type = event.target.value;
  //   // if (type === this.state.type) return;
  //   let type = (this.state.widgetType === HEADING) ? PARAGRAPH : HEADING;
  //
  //   // let newWidget1 = {...this.state.widget, widgetType: 'Paragraph'};
  //   // console.log(newWidget1);
  //   // let newWidget2 = {...this.state.widget, widgetType: PARAGRAPH};
  //   // console.log(newWidget2);
  //   let newWidget = {...this.state.widget, widgetType: type};
  //   console.log(newWidget);
  //
  //   this.props.updateWidget(this.state.widget.id, newWidget);
  // }

  updateName(event) {
    this.setState({name: event.target.value});
    // TODO: when you implement the server you will take a name
  }

  render() {
    return (
        <div className={"col container fluid wbdv-widget info"}>
          {/* TODO: dont forget to remove this */}
          <div>{JSON.stringify(this.state.widget)}</div>

          {this.state.widgetType === HEADING &&
          <HeadingWidgetComponent
              widgetType={HEADING}
              widget={this.state.widget}
              name={this.state.name}
              index={this.props.index}
              getLastElement={this.props.getLastElement}
              moveUp={this.props.moveUp}
              moveDown={this.props.moveDown}
              updateWidget={this.props.updateWidget}
              changeType={this.props.updateWidgetType}
              onDelete={this.props.onDelete}
              updateName={this.updateName.bind(this)}/>}
          {/*    TODO: update name*/}

          {this.state.widgetType === PARAGRAPH &&
          <ParagraphWidgetComponent
              widgetType={PARAGRAPH}
              widget={this.state.widget}
              name={this.state.name}
              index={this.props.index}
              getLastElement={this.props.getLastElement}
              moveUp={this.props.moveUp}
              moveDown={this.props.moveDown}
              updateWidget={this.props.updateWidget}
              changeType={this.changeType.bind(this)}
              onDelete={this.props.onDelete}
              updateName={this.updateName.bind(this)}/>}
        {/*TODO: update name*/}
        </div>
    )
  }
}

export default WidgetListItemComponent;
