import {
    CREATE_WIDGET,
    DELETE_WIDGET,
    UPDATE_WIDGET,
    FIND_ALL_WIDGETS_FOR_TOPIC,
    MOVE_WIDGET_UP,
    MOVE_WIDGET_DOWN,
    SAVE_ALL_WIDGETS
} from '../actions/widgetActions';

const widgetReducer = (state = {widgets: []}, action) => {
    switch(action.type) {
        case FIND_ALL_WIDGETS_FOR_TOPIC:
          return {widgets: action.widgets};
        case CREATE_WIDGET:
          return {widgets: [...state.widgets, action.newWidget]};
        case DELETE_WIDGET:
          return {widgets: state.widgets.filter(
            widget => widget.id !== action.widgetId
          )};
        case UPDATE_WIDGET:
          return {widgets: state.widgets.map(widget =>
            widget.id === action.widgetId ? action.widget : widget)};
        case MOVE_WIDGET_UP:
          var upIndex = state.widgets.indexOf(action.widget);
          state.widgets.swap(upIndex, upIndex - 1);
          return {widgets: state.widgets};
        case MOVE_WIDGET_DOWN:
          var downIndex = action.widgets.indexOf(action.widget);
          state.widgets.swap(downIndex, downIndex + 1);
          return {widgets: action.widgets};
        case SAVE_ALL_WIDGETS:
          return {widgets: action.widgets};
        default:
          return state
      }
};

Array.prototype.swap = function (x, y) {
  var b = this[x];
  this[x] = this[y];
  this[y] = b;
  return this;
}

export default widgetReducer;
