export const CREATE_TOPIC = 'CREATE_TOPIC';
export const DELETE_TOPIC = 'DELETE_TOPIC';
export const SET_SELECTED_LESSON = 'SET_SELECTED_LESSON';
export const UPDATE_TOPIC = 'UPDATE_TOPIC';

export const createTopic = (topic) => ({
  _id:(new Date()).getTime()+"",
  type: CREATE_TOPIC,
  newTopic: topic
});

export const deleteTopic = (topicId) => ({
  type: DELETE_TOPIC,
  topicId: topicId
});


export const setSelectedLesson = (lessons) => ({
  type: SET_SELECTED_LESSON,
  lessons: lessons
});

// not sure about title yet
export const updateTopic = (topic) => ({
  title: topic.title,
  type: UPDATE_TOPIC,
  topic: topic
});

export default {CREATE_TOPIC, DELETE_TOPIC, SET_SELECTED_LESSON, UPDATE_TOPIC,
  deleteTopic, createTopic, setSelectedLesson, updateTopic}
