import React from "react";
import {BrowserRouter as Router, Route} from "react-router-dom";
import '../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../CourseManager/CourseManager.css'
import CourseGridComponent from "./grid/CourseGridComponent";
import CourseTableComponent from "./row/CourseTableComponent";
import CourseHeadingComponent from "./CourseHeadingComponent";
import CourseSearchAndToggleComponent from "./CourseSearchAndToggleComponent";

const CourseListComponent =
    ({
      layout,
      updateFormState,
      addCourse,
      toggle,
      courses,
      showCourseEditor,
      hideCourseEditor,
      deleteCourse
    }) =>
        <div className="container-fluid">
          <CourseHeadingComponent/>
          <CourseSearchAndToggleComponent
              updateFormState={updateFormState}
              addCourse={addCourse}
              toggle={toggle}/>
          {layout === 'table' &&
          <CourseTableComponent
              courses={courses}
              showCourseEditor={showCourseEditor}
              hideCourseEditor={hideCourseEditor}
              deleteCourse={deleteCourse}/>}
          {layout == 'grid' &&
          <CourseGridComponent
              courses={courses}
              showCourseEditor={showCourseEditor}
              hideCourseEditor={hideCourseEditor}
              deleteCourse={deleteCourse}/>}
          {/*<CourseGridComponent*/}
          {/*deleteCourse={deleteCourse}*/}
          {/*courses={courses}/>}*/}
        </div>;

export default CourseListComponent
