const onSelect = (toggle, history, layout) => {
    this.props.toggle();
    this.props.history.push(`/${layout}`);
  };

const CourseSearchAndToggleComponent = ({updateFormState, addCourse, toggle, history}) =>
    <div id="wbdv-search" className="form-inline my-1 my-lg-0">
      <div>
        <input
            id="wbdv-search-bar"
            className="form-control mr-lg-2 wbdv-placeholder wbdv-field wbdv-new-course"
            onChange={(e) => updateFormState(e)}
            placeholder="New Course"
            aria-label="Search"/>
        <i onClick={() => addCourse()} id="wbdv-add-course"
           className="fa fa-plus-circle fa-3x"/>
      </div>
      <i onClick={() => onSelect()}
         className="fa fa-th fa-lg fa-3x wbdv-button wbdv-grid-layout"/>
         {/*<i onClick={() => toggle()}*/}
         {/*className="fa fa-th fa-lg fa-3x wbdv-button wbdv-grid-layout"/>*/}
    </div>;

export default CourseSearchAndToggleComponent;
