import React from "react";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {createModule, deleteModule, setSelectedCourse, updateModule} from '../../../actions/moduleActions'
import TopicPillItem from "./TopicPillItem";

const TopicPillComponent = (topics) =>
  <p>{JSON.stringify(topics)}</p>;
      // <ul class="nav nav-pills wbdv-margin-below wbdv-topic-pill">
      //   {topics.map(topic =>
      //       <TopicPillItem key={topic._id} topic={topic}/>
      //   )}
      //   <a id="wbdv-topic-add-btn" className="fa fa-plus-circle pt-1 fa-2x"
      //      onClick="window.location.href = '#';">
      //   </a>
      // </ul>;

// export default TopicPillComponent;


const stateToPropertyMapper = (state) => {
  return {
      topics: state.topics.topics
  }
}

const dispatchToPropertyMapper = (dispatch) => {
  return {
      createTopic: (courseId) =>
          moduleService.createModule(courseId, {title: 'New module'})
          .then(actualModule =>
              dispatch(createModule(actualModule))),

      setSelectedCourse: (courseId) =>
          moduleService.findModulesForCourse(courseId)
          .then(actualModules =>
              dispatch(setSelectedCourse(actualModules))),

      updateModule: (moduleId, module) =>
          moduleService.updateModule(moduleId, module)
          .then(updatedModule =>
              dispatch(updateModule(updatedModule))),

      deleteModule: (moduleId) =>
          moduleService.deleteModule(moduleId)
          .then(status =>
              dispatch(deleteModule(moduleId)))
  }
}

export default connect(
  stateToPropertyMapper,
  dispatchToPropertyMapper)
(TopicPillComponent)
