import React from 'react';
export const CREATE_MODULE = 'CREATE_MODULE';
export const DELETE_MODULE = 'DELETE_MODULE';
export const FIND_MODULES_FOR_COURSE = 'FIND_MODULES_FOR_COURSE';
export const UPDATE_MODULE = 'UPDATE_MODULE';

export const deleteModule = (moduleId) => ({
  type: DELETE_MODULE,
  moduleId: moduleId
});
export const createModule = (module) => ({
  title: 'New module',
  _id:(new Date()).getTime()+"",
  type: CREATE_MODULE,
  newModule: module
});
export const findModulesForCourse = (modules) => ({
  type: FIND_MODULES_FOR_COURSE,
  modules: modules
});
// not sure about title yet
export const updateModule = (module) => ({
  title: module.title,
  type: UPDATE_MODULE,
  modules: module
});

export default {CREATE_MODULE, DELETE_MODULE, FIND_MODULES_FOR_COURSE, UPDATE_MODULE,
  deleteModule, createModule, findModulesForCourse, updateModule}
