import React from "react";
import '../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './CourseEditor.css'
import {combineReducers, createStore} from "redux";
import {Provider} from "react-redux";

import moduleReducer from '../../reducers/moduleReducer.js'
import lessonReducer from '../../reducers/lessonReducer.js'
import topicReducer from '../../reducers/topicReducer.js'
import widgetReducer from '../../reducers/widgetReducer.js'

import ModuleListComponent from './Modules/ModuleListComponent';
import LessonTabComponent from "./Lessons/LessonTabComponent";
import TopicPillComponent from "./Topics/TopicPillComponent";
import WidgetListComponent from "./Widgets/WidgetListComponent";

const rootReducer = combineReducers({
  modules: moduleReducer,
  lessons: lessonReducer,
  topics: topicReducer,
  widgets: widgetReducer
});

const store = createStore(rootReducer);

class CourseEditorComponentStateful extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      moduleId: null,
      lessonId: null,
      topicId: null
    }
  }

  closeEditor() {
    this.props.hideCourseEditor();
    this.props.history.push("/");
  }

  onSelectModule(moduleId) {
    this.setState({moduleId: moduleId});
  }

  onSelectLesson(lessonId) {
    this.setState({lessonId: lessonId});
  }

  onSelectTopic(topicId) {
    this.setState({topicId: topicId});
  }

  render() {
    return (
        <Provider store={store}>
          <div className="container-fluid">

            <nav
                className="navbar navbar-expand-lg navbar-dark bg-dark text-white wbdv-margin-below wbdv-nav">
              <button
                  className="btn btn-sm pr-4 wbdv-white-icon wbdv-course-editor wbdv-close"
                  onClick={() => this.closeEditor()}>
                <i className="fa fa-times pt-1 fa-1.6x"/></button>
              <h2>Course Editor {this.props.courseTitle}</h2>
            </nav>

            <div className="row">
              <div className="col-4">
                <h4>Module List</h4>

                <ModuleListComponent
                    onSelectModule={this.onSelectModule(this)}
                    selectedModuleId={this.state.moduleId}
                    courseId={this.props.courseId}
                    history={this.props.history}/>

              </div>
              {this.props.match.params.moduleId &&
              <div className="col-8 container fluid wbdv-module-info">
                <h5>Lesson Tabs</h5>
                <LessonTabComponent
                    onSelectModule={this.onSelectModule(this)}
                    onSelectLesson={this.onSelectLesson(this)}
                    selectedModuleId={this.state.moduleId}
                    selectedLessonId={this.state.lessonId}
                    moduleId={this.props.moduleId}
                    courseId={this.props.courseId}
                    history={this.props.history}/>

                {this.props.match.params.lessonId &&
                <div>
                  <h5>Topic List</h5>
                  <TopicPillComponent
                      onSelectModule={this.onSelectModule(this)}
                      onSelectLesson={this.onSelectLesson(this)}
                      onSelectTopic={this.onSelectTopic(this)}
                      selectedModuleId={this.state.moduleId}
                      selectedLessonId={this.state.lessonId}
                      selectedTopicId={this.state.topicId}
                      lessonId={this.props.lessonId}
                      moduleId={this.props.moduleId}
                      courseId={this.props.courseId}
                      history={this.props.history}/>

                  {this.props.match.params.widgetId &&
                  <div className="row">
                    <WidgetListComponent
                        widgetId={this.props.widgetId}
                        topicId={this.props.topicId}
                        lessonId={this.props.lessonId}
                        moduleId={this.props.moduleId}
                        courseId={this.props.courseId}
                        history={this.props.history}/>
                  </div>
                  }
                </div>
                }
              </div>
              }
            </div>
          </div>
        </Provider>);
  }
}

export default CourseEditorComponentStateful;
