const stateToPropertyMapper = (state) => {
  return {
      widgets: state.widgets.widgets
  }
};

const dispatchToPropertyMapper = (dispatch) => {
  return {
      createWidget: (topicId) =>
          service.createWidget(topicId, {title: 'New widget', widgetType: 'HEADING'})
          .then(actualWidget =>
              dispatch(createWidget(actualWidget, topicId))),

      findAllWidgetsForTopic: (lessonId) =>
          service.findWidgetsForTopic(lessonId)
          .then(actualWidgets =>
              dispatch(findAllWidgetsForTopic(actualWidgets))),

      updateWidget: (widgetId, widget) =>
          service.updateWidget(widgetId, widget)
          .then(updatedWidget =>
              dispatch(updateWidget(updatedWidget))),

      deleteWidget: (widgetId) =>
          service.deleteWidget(widgetId)
          .then(status =>
              dispatch(deleteWidget(widgetId)))
  }
};

export default connect(
  stateToPropertyMapper,
  dispatchToPropertyMapper)
(WidgetListComponent)
