export const CREATE_LESSON = 'CREATE_LESSON';
export const DELETE_LESSON = 'DELETE_LESSON';
export const FIND_LESSONS_FOR_MODULE = 'FIND_LESSONS_FOR_MODULE';
export const UPDATE_LESSON = 'UPDATE_LESSON';

export const createLesson = (lesson) => ({
  _id:(new Date()).getTime()+"",
  type: CREATE_LESSON,
  newLesson: lesson
});

export const deleteLesson = (lessonId) => ({
  type: DELETE_LESSON,
  lessonId: lessonId
});

export const setSelectedModule = (lessons) => ({
  type: FIND_LESSONS_FOR_MODULE,
  lessons: lessons
});

export const updateLesson = (lesson) => ({
  title: lesson.title,
  type: UPDATE_LESSON,
  lesson: lesson
});

export default {CREATE_LESSON, DELETE_LESSON, FIND_LESSONS_FOR_MODULE, UPDATE_LESSON,
  deleteLesson, createLesson, findLessonsForModule, updateLesson}
