import React from "react";
import { connect } from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {
  createWidget,
  deleteWidget,
  findAllWidgetsForTopic,
  updateWidget,
  saveAllWidgets,
  moveWidgetUp,
  moveWidgetDown
} from '../../../actions/widgetActions'
import WidgetListItemComponent from './WidgetListItemComponent';
import SaveAndPreviewComponent from './SaveAndPreviewComponent';
import WidgetService from '../../../services/WidgetService'

const service = new WidgetService();


// TODO: to solve - how can you update the changes of the widget to render
// without saving it to the server (unless save all is pressed?)
class WidgetListComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      topWidget: null,
      bottomWidget: null,
      widgets: [],
    };
    // TODO: check if this only works when server is refreshed
    this.updateWidgets()
  }

  // TODO: check if correct (goes through all widgets and updates to server)
  onSaveAll() {
    this.props.saveAllWidgets(this.state.widgets, this.props.topicId);
  }

  getLastElement() {
    return this.state.widgets.length - 1;
  }

  async updateWidgets() {
    const widgets = await service.findWidgetsForTopic(this.props.topicId);
    this.setState({ widgets: widgets });
  }

  async addWidget() {
    // TODO: learn how to use save all for later - currentl this is all done on the server
    const newWidget = await this.props.createWidget(this.props.topicId);
    await this.updateWidgets();

  }

  async onDelete(widgetId) {
    await this.props.deleteWidget(widgetId);
    await this.updateWidgets();
  }

  swapElements(widgets, x, y) {
    var temp = widgets[x];
    widgets[x] = widgets[y];
    widgets[y] = temp;

    widgets[x].index = x;
    widgets[y].index = y;

    return widgets;
  }

  // TODO: check if you need to manipulate the state or if this is enough passing the props
  moveUp(widget) {
    // var index = this.state.widgets.indexOf(widget);
    // this.setState({widgets: this.swapElements(this.state.widgets, index, index - 1)});
    widget.index = this.state.widgets.indexOf(widget);
    this.props.moveWidgetUp(widget);
  }

  moveDown(widget) {
    // var index = this.state.widgets.indexOf(widget);
    // this.setState({widgets: this.swapElements(this.state.widgets, index, index + 1)});
    widget.index = this.state.widgets.indexOf(widget);
    this.props.moveWidgetDown(widget);
  }

  //TODO: is this how we update one widget?
  // we need to pass update to each item
  updateOneWidget(newWidget) {
    this.state.widgets.forEach(widget => {
      if (widget._id === newWidget._id) {
        return newWidget;
      }
    });
  }


  render() {
    return (
      <span>
        <SaveAndPreviewComponent
          saveAll={this.onSaveAll.bind(this)} />
        <div>
          {this.state.widgets && this.state.widgets.map((widget, index) =>
            <WidgetListItemComponent
              key={widget._id}

              index={index}
              getLastElement={this.getLastElement.bind(this)}

              widget={widget}

              moveUp={this.props.moveUp.bind(this)}
              moveDown={this.props.moveDown.bind(this)}

              onDelete={this.onDelete.bind(this)}
              updateWidget={this.props.updateWidget} // TODO: remove this later on
            />)
          }
          <div className="nav-item" onClick={() => this.addWidget()}>
            <i className="nav-link fa fa-plus-circle fa-2x" />
          </div>
        </div>
      </span>
    )
  }
}

const stateToPropertyMapper = (state) => {
  return {
    widgets: state.widgets.widgets
  }
};

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createWidget: (topicId) =>
      service.createWidget(topicId,
        {type: 'New widget', widgetType: 'Heading', index: this.state.widgets.length })
        .then(actualWidget =>
          dispatch(createWidget(actualWidget, topicId))),

    findAllWidgetsForTopic: (topicId) =>
      service.findWidgetsForTopic(topicId)
        .then(actualWidgets =>
          dispatch(findAllWidgetsForTopic(actualWidgets))),

    updateWidget: (widgetId, widget) =>
      service.updateWidget(widgetId, widget)
        .then(updatedWidget =>
          dispatch(updateWidget(updatedWidget))),

    deleteWidget: (widgetId) =>
      service.deleteWidget(widgetId)
        .then(status =>
          dispatch(deleteWidget(widgetId))),

    saveAllWidgets: (topicId, widgets) =>
      service.saveAllWidgets(topicId, widgets)
        .then(returnedWidgets =>
          dispatch(saveAllWidgets(returnedWidgets))),

    moveWidgetUp: (widget) =>
      dispatch(moveWidgetUp(widget)),

    moveWidgetDown: (widget) =>
      dispatch(moveWidgetDown(widget)),
  }
};

export default connect(
  stateToPropertyMapper,
  dispatchToPropertyMapper)
  (WidgetListComponent)
