import React from "react";

class CourseGridComponentStateful extends React.Component {
  constructor(props) {
    super(props)
  }
  editCourse = () => {
    this.setState(prevState => ({
      active: !prevState.active
    }))
  };

  saveCourse = () => {
    this.setState(prevState => ({
      active: !prevState.active
    }))
  };

  noOp = (event) => {
    event.stopPropagation();
  };

  updateForm = (event) => {
    event.stopPropagation();
  };

  render() {
    const initialText = this.props.course.title;

    return(
        <li className={`list-group-item ${this.state.active ? 'active' : ''}`}
            onClick={() => this.editCourse()}>
          {!this.state.active && this.props.course.title}
          {this.state.active &&
          <input onClick={this.noOp} value={initialText}/>}
          {
            this.state.active &&
            <span>
              <i onClick={this.updateForm} className="fa-2x fa fa-pencil"/>
              <i className="fa-2x fa fa-check" onClick={() =>this.saveCourse()}/>
              <i className="fa-2x fa fa-trash" onClick={(event) =>
                  this.props.deleteCourse(this.props.course)} />
            </span>
          }
        </li>
    )
  }
}

export default CourseGridComponentStateful;
