import React from "react";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import {createTopic, deleteTopic, setSelectedLesson, updateTopic} from '../../../actions/topicActions'
import TopicPillItem from "./TopicPillItem";
import TopicService from '../../../services/TopicService'

topicService = new TopicService();

const TopicPillComponent = (topics) =>
  <p>{JSON.stringify(topics)}</p>;
