import React from "react";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'

const SaveAndPreviewComponent = ({saveAll}) =>
    <div className="row row-right" id="wbdv-preview">

        <div className="custom-control custom-switch">
        <input type="checkbox" className="custom-control-input wbdv-preview-icon"
               id="customSwitch"/>
          <label className="custom-control-label wbdv-preview"
                 htmlFor="customSwitch">Preview</label>
        </div>
        <i className="fa fa-check fa-2x float-right" onClick={() => saveAll()}/>
    </div>;

export default SaveAndPreviewComponent;

