import React from 'react';
export const CREATE_TOPIC = 'CREATE_TOPIC';
export const DELETE_TOPIC = 'DELETE_TOPIC';
export const FIND_TOPICS_FOR_LESSON = 'FIND_TOPICS_FOR_LESSON';
export const UPDATE_TOPIC = 'UPDATE_TOPIC';

export const deleteTopic = (lessonId) => ({
  type: DELETE_TOPIC,
  lessonId: lessonId
});
export const createTopic = (lesson) => ({
  title: 'New lesson',
  _id:(new Date()).getTime()+"",
  type: CREATE_TOPIC,
  newTopic: lesson
});
export const findTopicsForLesson = (lessons) => ({
  type: FIND_TOPICS_FOR_LESSON,
  lessons: lessons
});
// not sure about title yet
export const updateTopic = (lesson) => ({
  title: lesson.title,
  type: UPDATE_TOPIC,
  lessons: lesson
});

export default {CREATE_TOPIC, DELETE_TOPIC, FIND_TOPICS_FOR_LESSON, UPDATE_TOPIC,
  deleteTopic, createTopic, findTopicsForLesson, updateTopic}
