import {
  CREATE_TOPIC,
  DELETE_TOPIC,
  UPDATE_TOPIC,
  FIND_TOPICS_FOR_LESSON}
  from "../actions/topicActions";

const initialState = {
  topics: []
};

const topicReducer = (state = initialState, action) => {
  switch(action.type) {
    case FIND_TOPICS_FOR_LESSON:
      return {topics: action.topics};
    case CREATE_TOPIC:
      return {topics: [...state.topics, action.newLesson]};
    case DELETE_TOPIC:
      return {topics: state.topics.filter(topic => topic._id !== action.topicId)};
    case UPDATE_TOPIC:
      return {topics: state.topics.map(topic =>
        topic._id === action.topicId ? action.topic : topic)};
    default:
      return state
  }
};

export default topicReducer
