import React from "react";
import {Link} from "react-router-dom";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../CourseManager.css';
import CourseService from '../../../services/CourseService.js'

const service = new CourseService();

class CourseRowComponentStateful extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      isActive: false,
      isEditing: false,
      course: this.props.course
    };
  }

  highlightCourse = (event) => {
    this.setState(prevState => ({isActive: !prevState.isActive}));
    this.noOp(event);
  };

  updateForm = (event) => {
    this.setState({
      isEditing: true,
      course: {
        ...this.state.course,
        title: event.target.value
      }
    })
  };

  saveCourse = (courseId, course, event) => {
    service.updateCourse(courseId, course)
    .then(status => this.updateForm(event))
  };

  noOp = (event) => {
    event.stopPropagation();
  };

  editing = () => {
    this.setState(prevState => ({isEditing: !prevState.isEditing}))
  };

  deleteCourse () {
    this.props.deleteCourse(this.state.course);
    this.state.course = '';
  };

  render() {

    return (
        <li className={`list-group-item ${this.state.isActive ? 'active' : ''}`}
            onClick={(event) => this.highlightCourse(event)}>
          {
            !this.state.isActive &&
            <div className={"form-inline"}>
            <Link to={`/course/${this.state.course._id}`}>
              <text onClick={() =>
                  this.props.onCourseTitleSelect(this.state.course.title)}>
                {this.state.course.title}
              </text>
            </Link>
              <div className={"form-inline wbdv-icon float-right"}>
                <i className="fa-2x fa fa-pencil"/>
                <i className="fa-2x fa fa-trash"
                   onClick={() => this.deleteCourse()}/>
              </div>
            </div>
          }

          {
            this.state.isActive &&
            <input
                onChange={this.updateForm}
                value={this.state.course.title}/>
          }
          {
            this.state.editing &&
            <div>
              <input onChange={this.updateForm()}
                     value={this.state.course.title}/>
              <i className="fa-2x fa fa-check"
                 onClick={(e) =>
                     this.saveCourse(this.state.course._id, this.state.course,
                         e)}/>
            </div>
          }
        </li>
    )
  }
}

export default CourseRowComponentStateful;
