import React from "react";
import {connect} from "react-redux";
import {Link} from 'react-router-dom'
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import '../../CourseManager/CourseManager.css'
import {createModule, deleteModule, findAllModulesForCourse, updateModule} from '../../../actions/moduleActions'
import ModuleService from '../../../services/ModuleService';
import ModuleListItem from './ModuleListItem';

const service = new ModuleService();

class ModuleListComponent extends React.Component {
    componentDidMount() {
      this.updateModules();
    }

    async updateModules() {
      const modules = await service.findModulesForCourse(this.props.courseId);
      this.setState({modules: modules});
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
      debugger;
        if (this.props.courseId !== prevProps.courseId || this.state !== prevState) {
            this.updateModules();
        }
    }

    updateModuleTitle(event) {
      this.setState(prevState => ({
        module: {
          ...prevState.module,
          title: event.target.value
        }}))
    }

    state = {
      selectedModule: null,
      editingModuleId: '',
      modules: []
    };

    // make highlighted, change URL to inclide module,
    // and render all lessons related to the module
    toggleSelectedModule(module) {
      if (this.state.selectedModule === module) {
        this.setState({selectedModule: null});
        this.props.history.push(`/course/${this.props.courseId}`)
      } else {
        this.setState({selectedModule: module});
      }
      // this.props.history.push(`/course/${this.props.courseId}/module/${module._id}`);
    }

    render() {
        return (
          <div>
            <ul className="list-group wbdv-module-list">
                {this.state.modules && this.state.modules.map(module =>
                    <li className={`list-group-item wbdv-module-item wbdv-module-item-title
                      ${this.state.selectedModule === module ? 'active' : ''}`}
                      onClick={() => this.toggleSelectedModule(module)} key={module._id}>

                          {this.state.editingModuleId !== module._id &&
                            <Link to={`/course/${this.props.courseId}/module/${module._id}`}>{module.title}</Link>}

                          {/* {this.state.editingModuleId === module._id &&
                          <div>
                          <input onChange={(e) => this.updateModuleTitle(e)}
                            value={this.state.module.title}/>
                            <i className="fa-2x fa fa-check"
                            onClick={() => {
                              this.props.updateModule(this.state.module).then(() =>
                              this.setState({editingModuleId: ''}))}}/>
                          </div>} */}

                      <span className="float-right">
                        <i className="fa fa-trash fa-2x"
                          onClick={() => this.props.deleteModule(module._id)}/>
                        <i className="fa fa-pencil fa-2x"
                          onClick={() => this.setState(prevState => ({editingModuleId: ''}))}/>
                      </span>
                    </li>
                )}
              </ul>
                <i className="fa fa-plus-circle fa-3x wbdv-add"
                  onClick={() => this.props.createModule(this.props.courseId)}/>
            </div>
        );
    }
}

const stateToPropertyMapper = (state) => {
    return {
        modules: state.modules.modules
    }
}

const dispatchToPropertyMapper = (dispatch) => {
    return {
        createModule: (courseId) =>
            service.createModule(courseId, {title: 'New module'})
            .then(actualModule =>
                dispatch(createModule(actualModule, courseId))),

        findAllModulesForCourse: (courseId) =>
            service.findModulesForCourse(courseId)
            .then(actualModules =>
                dispatch(findAllModulesForCourse(actualModules))),
        // TODO: Instead of first getting the modules for course then dispatching the list of modules,
        // you should instead dispatch something like this:
        // dispatch({ type: SET_SELECTED_COURSE, courseId: courseId })

        updateModule: (moduleId, module) =>
            service.updateModule(moduleId, module)
            .then(updatedModule =>
                dispatch(updateModule(updatedModule))),

        deleteModule: (moduleId) =>
            service.deleteModule(moduleId)
            .then(status =>
                dispatch(deleteModule(moduleId)))
    }
}

export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper)
(ModuleListComponent)
