import React from "react";
import WidgetBarComponent from "./WidgetBarComponent";

class ParagraphWidgetComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      paragraphText: ''
    }
  }

  componentDidMount() {
  }

  updateParagraphText(event) {
    this.setState({paragraphText: event.target.value});
  }

  render() {
    return (
        <span>
          <WidgetBarComponent
              type={this.props.type}
              changeType={this.props.changeType}
              onDeleteWidget={this.props.onDelete}/>

          {/*    TODO: make resizable*/}
          <div className="form-group row">
            <div className="col-sm-10">
                 {/*id={"wbdv-text-box"}>*/}
                 {/*for="wbdv-input-area">*/}
              <input type="text"
                     // id={"wbdv-input-area"}
                     className="form-control wbdv-placeholder"
                     placeholder="Paragraph text"
                     onChange={(e) => this.updateParagraphText(e)}/>
            </div>
          </div>

          <div className="form-group row">
            <div className="col-sm-10">
              <input className="form-control wbdv-placeholder"
                     placeholder="Widget name"
                     onClick={(e) => this.props.updateName(e)}/>
            </div>
          </div>

          <div className="form-group row">
            <h4 className="col-sm-10">Preview</h4>
          </div>
          <div className="form-group row">
            {this.state.paragraphText}
          </div>
        </span>
    )
  }
}

export default ParagraphWidgetComponent;
