class LessonService {
  createLesson = (moduleId, lesson) =>
      fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/modules/${moduleId}/lessons`, {
        method: 'POST',
        body: JSON.stringify(lesson),
        headers: {
          'content-type': 'application/json'
        }
      }).then(response => response.json());

  findLessonsForModule = async (moduleId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/modules/${moduleId}/lessons`);
    return await response.json();
  }

  updateLesson = async (lessonId, lesson) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}`, {
      method: 'PUT',
      body: JSON.stringify(lesson),
      headers: {'content-type': 'applications/json'}
    });
    return await response.json();
  }

  deleteLesson = async (lessonId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}`, {
      method: 'DELETE'
    });
    return await response.json();
  }
}

export default LessonService;
