import React from "react";
import WidgetBarComponent from "./WidgetBarComponent";

const Heading = ({size, text}) =>
    <span>
      {size === 1 && <h1>{text}</h1>}
      {size === 2 && <h2>{text}</h2>}
      {size === 3 && <h3>{text}</h3>}
      {size === 4 && <h4>{text}</h4>}
      {size === 5 && <h5>{text}</h5>}
      {size === 6 && <h6>{text}</h6>}
    </span>;

class HeadingWidgetComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      headingText: '',
      headingSize: 1
    }
  }

  componentDidMount() {
  }

  updateHeadingText(event) {
    this.setState({headingText: event.target.value});
  }

  updateHeadingSize(event) {
    const size = event.target.value.slice(-1);
    this.setState({headingSize: size});
    this.setState({headingTag: this.getHeadingTag()});
  }

  render() {
    return (
        <span>
          <WidgetBarComponent
              type={this.props.type}
              changeType={this.props.changeType}
              onDeleteWidget={this.props.onDeleteWidget}/>

          {/*UPDATE HEADING TEXT HERE*/}
          <div className="form-group row">
            <div className="col-sm-10">
              <input className="form-control wbdv-placeholder"
                     placeholder="Heading text"
                     onChange={(e) => this.updateHeadingText(e)}/>
            </div>
          </div>

          {/*UPDATE SIZE HERE*/}
          <div className="form-group row">
            <div className="col-sm-10">
              {/*<input className="form-control wbdv-placeholder"*/}
              {/*       placeholder="Heading1"/>*/}

              <select className="form-control"
                      defaultValue={`Heading ${this.state.headingSize}`}
                      onClick={(event) => this.updateHeadingSize(event)}>
              <option>Heading 1</option>
              <option>Heading 2</option>
              <option>Heading 3</option>
              <option>Heading 4</option>
              <option>Heading 5</option>
              <option>Heading 6</option>
            </select>
            </div>
          </div>

          <div className="form-group row">
            <div className="col-sm-10">
              <input className="form-control wbdv-placeholder"
                     placeholder="Widget name"/>
            </div>
          </div>

          <div className="form-group row">
            <h4 className="col-sm-10">Preview</h4>
          </div>
          <div className="form-group row">
            {/*<h2 className="col-sm-10">Heading text</h2>*/}
            <Heading size={this.state.headingSize}
                     text={this.state.headingText}/>
          </div>
        </span>
    )
  }
}

export default HeadingWidgetComponent;
