const oldUrl = 'https://wbdv-generic-server.herokuapp.com/api/001367674';
const localUrl = 'http://localhost:8080/api';
const herokuUrl = 'https://serene-island-60523.herokuapp.com/api';

class TopicService {
  async createTopic (lessonId, topic) {
    const response = await fetch(`${localUrl}/lessons/${lessonId}/topics`, {
      method: 'POST',
      body: JSON.stringify(topic),
      headers: {'content-type': 'application/json'}
    });
    return await response.json();
  }

  async findTopicsForLesson (lessonId) {
    const response = await fetch(`${localUrl}/lessons/${lessonId}/topics`);
    return await response.json();
  }

  async updateTopic (topicId, topic) {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
      method: 'PUT',
      body: JSON.stringify(topic),
      headers: {'content-type': 'application/json'}
    });
    return await response.json();
  }

  async deleteTopic (topicId) {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
      method: 'DELETE'
    });
    return await response.json();
  }
}

export default TopicService;
