import React from 'react';
import {connect} from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css';
import LessonTabItem from './LessonTabItem';
import {createLesson, deleteLesson, findAllLessonsForModule, updateLesson} from '../../../actions/lessonActions'
import lessonService from '../../../services/LessonService';

// const LessonTabComponent = (lessons) =>
//     <p>{JSON.stringify(lessons)}</p>;
//     <div>
//       <div class="collapse navbar-collapse">
//         <ul className="navbar-nav">
//           {
//             lessons.map(lesson =>
//                 <LessonTabItem key={lesson._id} lesson={lesson}/>)
//           }
//         </ul>
//       </div>
//       <button type="button" onClick="window.location.href='#';"
//               className="btn btn-default rounded-circle wbdv-white-icon wbdv-new-page-btn">
//         <i className="fa fa-plus-circle pt-1 fa-2x"></i>
//       </button>
//     </div>;

// export default LessonTabComponent

const service = new lessonService();

class LessonTabComponent extends React.Component {
  componentDidMount() {
    this.updateLessons();
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if(this.props.moduleId !== prevProps.moduleId) {
      this.updateLessons();
    }
  }

  async updateLessons() {
    const lessons = await service.findLessonsForModule(this.props.moduleId);
    this.setState({lessons: lessons});
  }

//   updateLessonTitle(event) {
//     this.setState(prevState => ({
//       lesson: {
//         ...prevState.lesson,
//         title: event.target.value
//       }}))
//   }

  state = {
    selectedLesson: null,
    editinglessonId: null,
    lessons: [],
  };

  render() {
    // return (<div>{JSON.stringify(this.state.lessons)}</div>)
    return (
        <div>
        <div class="collapse navbar-collapse">
            <ul className="navbar-nav">
            {this.state.lessons &&   
                this.state.lessons.map(lesson =>
                    this.setState({selectedLesson: lesson}))
                    // <LessonTabItem key={lesson._id} lesson={lesson}/>
            }
            </ul>
        </div>

        <button type="button"
                className="btn btn-default rounded-circle wbdv-white-icon wbdv-new-page-btn">
            <i className="fa fa-plus-circle pt-1 fa-2x"/>
        </button>
        </div>
        );
  }
}

// export default LessonTabComponent;

const stateToPropertyMapper = (state) => {
  return {
    lessons: state.lessons.lessons
  }
}

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createLesson: (moduleId) =>
        lessonService.createLesson(moduleId, {title: 'New lesson'})
        .then(actuallesson =>
            dispatch(createLesson(actuallesson, moduleId))),

    findAllLessonsForModule: (moduleId) =>
        lessonService.findLessonsForModule(moduleId)
        .then(actuallessons =>
            dispatch(findAllLessonsForModule(actuallessons))),

    updateLesson: (lessonId, lesson) =>
        lessonService.updateLesson(lessonId, lesson)
        .then(updatedlesson =>
            dispatch(updateLesson(updatedlesson))),

    deleteLesson: (lessonId) =>
        lessonService.deleteLesson(lessonId)
        .then(status =>
            dispatch(deleteLesson(lessonId)))
  }
};

export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper)
(LessonTabComponent)
