import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'

// const LessonTabItem = ({lesson}) =>
//     <li className="nav-item">
//       <a className="nav-link wbdv-page-tab" href="#">{lesson.title}</a>
//     </li>;

class LessonTabItem extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      isEditing: false,
      title: props.lesson.title
    };
  }

  onEdit() {
    this.setState({isEditing: true});
    this.props.onSelect();
  }

  onChange(event) {
    this.setState({title: event.target.value});
  }

  onSave() {
    const newLesson = {...this.props.lesson, title: this.state.title};
    console.log('saving new lesson: ' + JSON.stringify(newLesson));
    this.props.onSave(newLesson);
    this.setState({isEditing: false});
  }

  render() {
    return (
      <li className="nav-item">
        <a className={`nav-link ${this.props.isSelected ? 'active' : ''}`}
          onClick={() => this.props.onSelect()} key={this.props.lesson._id}>
</a>
          {!this.state.isEditing && 
            <div>
              {this.state.title}
              <i className="fa fa-pencil" onClick={() => this.onEdit()}/>
            </div>}

          {this.state.isEditing && 
            <div>
              <input type="text" className="typing-container"
                value={this.state.title}
                onChange={(e) => this.onChange(e)}/>

              <span className="float-right">
                <i className="fa fa-trash" onClick={() => this.props.onDelete()}/>
                <i className="fa fa-check" onClick={() => this.onSave()}/>
              </span>
            </div>
          }
        {/* </a> */}
      </li>
    );
  }
}

export default LessonTabItem;


