export const CREATE_WIDGET = 'CREATE_WIDGET';
export const DELETE_WIDGET = 'DELETE_WIDGET';
export const FIND_ALL_WIDGETS_FOR_TOPIC = 'FIND_ALL_WIDGETS_FOR_TOPIC';
export const UPDATE_WIDGET = 'UPDATE_WIDGET';
export const MOVE_WIDGET_UP = 'MOVE_WIDGET_UP';
export const MOVE_WIDGET_DOWN = 'MOVE_WIDGET_DOWN';
export const SAVE_ALL_WIDGETS = 'SAVE_ALL_WIDGETS';


export const createWidget = (widget, topicId) => ({
  title: 'New Widget',
  widgetType: widget.type,
  _id:(new Date()).getTime()+"",
  topicId: topicId,
  type: CREATE_WIDGET,
  newWidget: widget
  });

  export const deleteWidget = (widgetId) => ({
    type: DELETE_WIDGET,
    widgetId: widgetId
  });

  export const findAllWidgetsForTopic = (widgets) => ({
    type: FIND_ALL_WIDGETS_FOR_TOPIC,
    widgets: widgets
  });

  export const updateWidget = (widget) => ({
    widgetType: widget.type,
    type: UPDATE_WIDGET,
    widget: widget
  });

  export const moveWidgetUp = (widget, widgets) => ({
    index: widget.index,
    type: MOVE_WIDGET_UP,
    widget: widget
  })

  export const moveWidgetDown = (widget) => ({
    index: widget.index,
    type: MOVE_WIDGET_DOWN,
    widget: widget
  })

  export const saveAllWidgets = (widgets) => ({
    widgets: widgets,
    type: SAVE_ALL_WIDGETS
  })



  export default {CREATE_WIDGET, DELETE_WIDGET, FIND_ALL_WIDGETS_FOR_TOPIC, UPDATE_WIDGET, MOVE_WIDGET_UP, MOVE_WIDGET_DOWN, SAVE_ALL_WIDGETS,
    deleteWidget, createWidget, findAllWidgetsForTopic, updateWidget, moveWidgetUp, moveWidgetDown, saveAllWidgets}
