import React from "react";
import '../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './CourseEditor.css'
import {combineReducers, createStore} from "redux";
import {Provider} from "react-redux";

import moduleReducer from '../../reducers/moduleReducer.js'
import lessonReducer from '../../reducers/lessonReducer.js'
import topicReducer from '../../reducers/topicReducer.js'
import ModuleListComponent from './Modules/ModuleListComponent';
import LessonTabComponent from "./Lessons/LessonTabComponent";
import TopicPillComponent from "./Topics/TopicPillComponent";

import ModuleService from "../../services/ModuleService";
import LessonService from "../../services/LessonService";
import TopicService from "../../services/TopicService";

const moduleService = new ModuleService();
const lessonService = new LessonService();
const topicService = new TopicService();

const rootReducer = combineReducers({
  modules: moduleReducer,
  lessons: lessonReducer,
  topics: topicReducer
});

const store = createStore(rootReducer);

class CourseEditorComponentStateful extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modules: []
    };
  }

  closeEditor() {
    this.props.hideCourseEditor();
    this.props.history.push("/");
  }

  async componentDidMount() {
    const modules = await moduleService.findModulesForCourse(this.courseId);
    this.setState({modules: modules});
  }

  render() {
    return (
    <Provider store={store}>
      <div className="container-fluid">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark text-white wbdv-margin-below wbdv-nav">
        <button className="btn btn-sm pr-4 wbdv-white-icon wbdv-course-editor wbdv-close"
                onClick={() => this.closeEditor(this.props.hideCourseEditor, this.props.history)}>
          <i className="fa fa-times pt-1 fa-1.6x"/></button>

          <h2>Course Editor {this.props.match.params.courseId}</h2>
        </nav>

        <div className="row">
          <div className="col-4">
            <h4>Module List</h4>
            <ModuleListComponent
              modules={this.state.modules}
              courseId={this.props.match.params.courseId}
              history={this.props.history}/>
          </div>
          <div className="col-8">
            {/* TODO(mocaa7): Replace this with the real modules from the server. */}
            <LessonTabComponent
              lessons={lessonService.findLessonsForModule(this.props.match.params.moduleId)}
              moduleId={this.props.match.params.moduleId}
              courseId={this.props.match.params.courseId}/>
            <TopicPillComponent
              topics={topicService.findTopicsForLesson(this.props.match.params.lessonId)}
              lessonId={this.props.match.params.lessonId}
              moduleId={this.props.match.params.moduleId}
              courseId={this.props.match.params.courseId}/>
          </div>
        </div>
      </div>
    </Provider>);
  }
}

export default CourseEditorComponentStateful;
