class TopicService {
  async createTopic (lessonId, topic) {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}/topics`, {
      method: 'POST',
      body: JSON.stringify(topic),
      headers: {'content-type': 'application/json'}
    });
    return await response.json();
  }

  findTopicsForLesson = async (lessonId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/lessons/${lessonId}/topics`);
    return await response.json();
  }

  updateTopic = async (topicId, topic) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
      method: 'PUT',
      body: JSON.stringify(topic),
      headers: {'content-type': 'applications/json'}
    });
    return await response.json();
  }

  deleteTopic = async (topicId) => {
    const response = await fetch(`https://wbdv-generic-server.herokuapp.com/api/001367674/topics/${topicId}`, {
      method: 'DELETE'
    });
    return await response.json();
  }
}

export default TopicService;
