import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'
import ModuleService from '../../../services/ModuleService';

const service = new ModuleService();

class ModuleListItem extends React.Component {

  state = {
    isEditing: false,
    title: null,
  }

  // constructor(props) {
  //   super(props);

  //   this.setState({
  //     isActive: props.isActive,
  //     isEditing: props.isEditing,
  //   });
  // }

  // componentDidMount() {
  //   this.getLatestModule(this.props.moduleId);
  // }

  // async getLatestModule(moduleId) {
  //   const module = await service.getModule(moduleId);
  //   this.setState({module: module});
  // }

  // onSelect() {
  //   this.props.setSelectedModule(this.props.moduleId);
  //   // this.setState({isActive: true});
  //   // this.props.resetOtherModules(this.props.moduleId);
  // }

  onEdit() {
    this.setState({isEditing: true});
    this.props.onSelect();
    // this.setState({isEditing: true});
    // this.props.resetOtherModules(this.props.moduleId);
  }

  onChange(event) {
    this.setState({editedTitle: event.target.value});
  }

  // onDelete() {
  //   this.props.deleteModule(this.state.module._id);
  // }

  onSave() {
    this.props.onSave(this.state.editedTitle);
  }

  // I need:
  /*
  {
    isSelected: boolean
    module: object
    onSelect: closure that selects current module
    onDelete: closure that deletes current module
    onSave: closure that updates current module and refreshes everything
  }
  */

  render() {
    return (
      <li className={`list-group-item wbdv-module-item wbdv-module-item-title
        ${this.props.isSelected ? 'active' : ''}`}
        onClick={() => this.props.onSelect()} key={module._id}>

        {!this.state.isEditing &&
          <div>
            <span>{this.props.module.title}</span>
            <span className="float-right">
              <i className="fa fa-trash fa-2x"
                onClick={() => this.props.onDelete()}/>
              <i className="fa fa-pencil fa-2x"
                onClick={() => this.onEdit()}/>
            </span>
          </div>
        }
        {this.state.isEditing &&
          <div>
            <input type="text" className="typing-container"
            value={this.props.module.title}
            onChange={(e) => this.onChange(e)} />

            <span className="float-right">
              <i className="fa fa-check fa-2x"
                onClick={() => this.onSave()}/>
            </span>
          </div>
        }
      </li>
    );
  }
}

export default ModuleListItem;
