import React from 'react'
import {BrowserRouter as Router, Route} from "react-router-dom";
import '../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../node_modules/font-awesome/css/font-awesome.min.css';
import '../components/CourseManager/CourseManager.css'
import CourseHeadingComponent from "../components/CourseManager/CourseHeadingComponent";
import CourseService from "../services/CourseService";
import CourseSearchAndToggleComponent from "../components/CourseManager/CourseSearchAndToggleComponent";
import CourseEditorComponentStateful from '../components/CourseEditor/CourseEditorComponentStateful';
import CourseListComponent from '../components/CourseManager/CourseListComponent.js';

const courseService = new CourseService();

class CourseManagerContainer extends React.Component {

  /**
   * the state of the page
   * @type {{layout: string, courses: Array, newCourseTitle: string}}
   */
  state = {
    layout: 'table',
    editingCourse: false,
    newCourseTitle: 'New Course',
    courses: []
  };

  componentDidMount = async () => {
    const allCourses = await courseService.findAllCourses();
    this.setState({courses: allCourses})
  };

  /**
   * deletes the course on the server, then finds all courses and sets the
   * state of those courses (updates the state and re-renders it)
   *
   * @param courseToDelete the course to delete
   */
  deleteCourse = async (deletedCourse) => {
    const status = await courseService.deleteCourse(deletedCourse._id);
    const courses = await courseService.findAllCourses();
    this.setState({courses: courses})
  };

  /**
   * creates a course on the server with the newCourse title as per current
   * state on the page, then gets all the courses from the server, and sets
   * the state (updates the current state and renders it)
   */
  addCourse = async () => {
    debugger;
    await courseService.createCourse({title: this.state.newCourseTitle});
    const actual = await courseService.findAllCourses();
    this.setState({courses: actual});
  }

  /**
   * When clicked on the toggle button, then this function executes.
   * If currently grid, change to table, and vice versa.
   */
  toggle = () => {
    this.setState(prevState => ({
      layout: prevState.layout === 'grid' ? 'table': 'grid'
    }))
  };

  /**
   * When clicking saveCourse button:
   * updates the title of the course (as done in the function below).
   * TODO: DELETE BELOW
   * Then shows the title as text, hides the save course button, and shows the
   * delete and edit buttons again
   */
  updateFormState = (event) => {
    console.log(event.target.value);
    this.setState({newCourseTitle: event.target.value});
  };

  /**
   * when clicking on the pencil on the row of a given course
   * shows the title in an input field, hides the delete and edit button,
   * shows the save course button
   * TODO: CHECK IF THIS IS REALLY NEEDED
   */
  editCourse = (course) => {
    this.setState(prevState => ({
      courses: prevState.courses.map(c => {
        c.editing = false;
        c.editing = (c._id === course._id);
        return c
      })}))
  };

  showCourseEditor = () =>
      this.setState({
        editingCourse: true
      });

  hideCourseEditor = () =>
      this.setState({
        editingCourse: false
      });

  // updateCourseTitle = (courseToUpdate) => {
  //   courseService.updateCourse(courseToUpdate._id, courseToUpdate)
  //   .then(status => courseService.findAllCourses())
  //   .then(courses => this.setState({courses: courses}));
  // };

  // this.setState(prevState => ({
  //   courses: prevState.courses.filter(course => course._id !== deletedCourse._id)
  // }))
  // saveCourseTitle = (course) => {
  //   this.setState(prevState => ({
  //     courses: prevState.courses.map(c => {
  //       if (c._id === course._id) {
  //         c.title = course.title;
  //       }})}))
  // };

  getAllCourses = () =>
    courseService.findAllCourses().then(
        storedCourses => this.setState({courses: storedCourses})
    );

  render() {
    return (
        <div className="container-fluid">
          {/* <CourseHeadingComponent/>
          <CourseSearchAndToggleComponent
            layout={this.layout}
            updateFormState = {this.updateFormState}
            addCourse = {this.addCourse}
            toggle = {this.toggle}/> */}
          <Router>
            <Route
                path="/course/:courseId"
                exact={true}
                render={(props) =>
                    <CourseEditorComponentStateful
                      {...props}
                      courseId={props.match.params.courseId}
                      hideCourseEditor={this.hideCourseEditor}/>
                }/>
            <Route
                path="/course/:courseId/module/:moduleId"
                exact={true}
                render={(props) =>
                    <CourseEditorComponentStateful
                        {...props}
                        moduleId={props.match.params.moduleId}
                        courseId={props.match.params.courseId}
                        hideCourseEditor={this.hideCourseEditor}/>
                }/>
            <Route
                path="/course/:courseId/module/:moduleId/lesson/:lessonId"
                exact={true}
                render={(props) =>
                    <CourseEditorComponentStateful
                        {...props}
                        lessonId={props.match.params.lessonId}
                        moduleId={props.match.params.moduleId}
                        courseId={props.match.params.courseId}
                        hideCourseEditor={this.hideCourseEditor}/>
                }/>
            <Route
                path="/course/:courseId/module/:moduleId/lesson/:lessonId/topic/:topicId"
                exact={true}
                render={(props) =>
                    <CourseEditorComponentStateful
                        {...props}
                        topicId={props.match.params.topicId}
                        lessonId={props.match.params.lessonId}
                        moduleId={props.match.params.moduleId}
                        courseId={props.match.params.courseId}
                        hideCourseEditor={this.hideCourseEditor}/>
                }/>
            <Route
                path="/"
                exact={false}
                render={() =>
                  <CourseListComponent
                      layout={this.state.layout}
                      updateFormState = {this.updateFormState}
                      addCourse = {this.addCourse}
                      toggle = {this.toggle}
                      courses={this.state.courses}
                      showCourseEditor={this.showCourseEditor}
                      hideCourseEditor={this.hideCourseEditor}
                      deleteCourse={this.deleteCourse}/>
                }/>
          </Router>
        </div>
    );
  }
}

export default CourseManagerContainer
