import React from "react";
import WidgetBarComponent from "./WidgetBarComponent";
import WidgetListItemComponent from "./WidgetListItemComponent";

const HEADING_TYPE = 'Heading';

const Heading = ({size, text}) =>
    <div className="form-group row">
      {size === 'Heading 1' && <h1>{text}</h1>}
      {size === 'Heading 2' && <h2>{text}</h2>}
      {size === 'Heading 3' && <h3>{text}</h3>}
      {size === 'Heading 4' && <h4>{text}</h4>}
      {size === 'Heading 5' && <h5>{text}</h5>}
      {size === 'Heading 6' && <h6>{text}</h6>}
    </div>;

class HeadingWidgetComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      widget: props.widget,
      size: props.size,
      name: props.name
    };

    this.state.widget.size = this.props.size;
    this.state.widget.widgetType = HEADING_TYPE;
  }

  updateContent(event) {
    this.setState({text: event.target.value});
  }

  updateHeadingSize(event) {
    this.setState({size: event.target.value});
    this.state.widget.size = this.state.size;
  }

  render() {
    return (
        <span>
          <WidgetBarComponent
              type={this.props.widgetType}
              widget={this.state.widget}
              index={this.props.index}
              getLastElement={this.props.getLastElement}
              moveUp={this.props.moveUp}
              moveDown={this.props.moveDown}
              updateWidgetType={this.props.updateWidgetType}
              onDelete={this.props.onDelete}
          />

          <div className="form-group row">
            <div className="col-sm-10">
              <input className="form-control wbdv-placeholder"
                     placeholder="Heading text"
                     defaultValue={this.props.content}
                     onChange={(e) => this.props.updateWidgetContent(e)}/>
            </div>
          </div>

          <div className="form-group row">
            <div className="col-sm-10">

              <select className="form-control"
                      defaultValue={`${this.props.headingSize}`}
                      onClick={(event) => this.props.updateHeadingSize(event)}>
              <option>Heading 1</option>
              <option>Heading 2</option>
              <option>Heading 3</option>
              <option>Heading 4</option>
              <option>Heading 5</option>
              <option>Heading 6</option>
            </select>
            </div>
          </div>

          <div className="form-group row">
            <div className="col-sm-10">
              <input className="form-control wbdv-placeholder"
                     defaultValue={this.props.name}
                     placeholder="Widget name"
                     onClick={(e) => this.props.updateWidgetName(e)}/>
            </div>
          </div>

          <div className="form-group row">
            <h4 className="col-sm-10">Preview</h4>
          </div>
            <Heading size={this.props.size}
                     text={this.props.widget.text}
            />
        </span>
    )
  }
}

export default HeadingWidgetComponent;
