// export default LessonTabComponent


// const lessonService = new lessonService();

class LessonTabComponent extends React.Component {
  componentDidMount() {
    lessonService.findLessonsForModule(this.props.moduleId);
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if(this.props.lessonId !== prevProps.lessonId) {
      lessonService.findLessonsForModule(this.props.moduleId);
    }
  }

  updateLessonTitle(event) {
    this.setState(prevState => ({
      lesson: {
        ...prevState.lesson,
        title: event.target.value
      }}))
  }

  state = {
    selectedlessonId: '',
    editinglessonId: '',
    lesson: {
      title: '',
      _id: ''
    }
  };

  render() {
    return (<p>{JSON.stringify(lessons)}</p>);
  }
}

const stateToPropertyMapper = (state) => {
  return {
    lessons: state.lessons.lessons
  }
}

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createLesson: (moduleId) =>
        lessonService.createLesson(moduleId, {title: 'New lesson'})
        .then(actuallesson =>
            dispatch(createLesson(actuallesson))),

    findLessonsForModule: (moduleId) =>
        lessonService.findLessonsForModule(moduleId)
        .then(actuallessons =>
            dispatch(findLessonsForModule(actuallessons))),

    // TODO: pass moduleId or lesson?
    updateLesson: (lessonId, lesson) =>
        lessonService.updateLesson(lessonId, lesson)
        .then(updatedlesson =>
            dispatch(updateLesson(updatedlesson))),

    deleteLesson: (lessonId) =>
        lessonService.deleteLesson(lessonId)
        .then(status =>
            dispatch(deleteLesson(lessonId)))
  }
};

export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper)
(LessonTabComponent)
