import React from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css'
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css'

class ModuleListItem extends React.Component {

  state = {
    isActive: false,
    module: null,
  }

  setActive() {
    this.isActive = true;
    this.props.setSelectedModule(this.state.module._id);
  }

  render() {
    return (
      <li className={`list-group-item wbdv-module-item wbdv-module-item-title
        ${this.state.isActive ? 'active' : ''}`}
        onClick={() => this.toggleSelectedModule(module)} key={module._id}>

            {/* {this.state.editingModuleId === module._i                         <div>
            <input onChange={(e) => this.updateModuleTitle(e)}
              value={this.state.module.title}/>
              <i className="fa-2x fa fa-check"
              onClick={() => {
                this.props.updateModule(this.state.module).then(() =>
                this.setState({editingModuleId: ''}))}}/>
            </div>} */}
        {this.state.editingModule !== module &&
          <div>{module.title}
            <span className="float-right">
              <i className="fa fa-trash fa-2x"
                onClick={() => this.deleteModule(module._id)}/>
              <i className="fa fa-pencil fa-2x"
                onClick={() => this.setState({editingModule: module})}/>
            </span>
          </div>
        }
        {this.state.editingModule === module &&
          <div>
            <input type="text"
            className="typing-container" value={module.title} />

            <span className="float-right">
              <i className="fa fa-check fa-2x"
                onClick={() => this.saveModule(module)}/>
            </span>
          </div>
        }
      </li>
    );
  }
}

export default ModuleListItem;
