import React from "react";
import { connect } from "react-redux";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../../CourseEditor/CourseEditor.css';
import {
  createWidget,
  deleteWidget,
  findAllWidgetsForTopic,
  updateWidget,
  updateWidgetType,
  saveAllWidgets,
  moveWidgetUp,
  moveWidgetDown
} from '../../../actions/widgetActions';
import store from '../CourseEditorComponentStateful';
import HeadingWidgetComponent from './HeadingWidgetComponent';
import ParagraphWidgetComponent from './ParagraphWidgetComponent';
import WidgetListItemComponent from './WidgetListItemComponent';
import SaveAndPreviewComponent from './SaveAndPreviewComponent';
import WidgetService from '../../../services/WidgetService';
import WidgetBarComponent from "./WidgetBarComponent";

const service = new WidgetService();

const HEADING = 'Heading';
const PARAGRAPH = 'Paragraph';
const IMAGE = 'Image';
const LIST = 'List';

const DEFAULT_HEADING_SIZE = 'Heading 1';
const DEFAULT_HEADING_TEXT = 'Heading text';
const DEFAULT_PARAGRAPH_TEXT = 'Paragraph text';
const DEFAULT_LIST_TYPE = false;

class WidgetListComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      widgets: [],
    };

    this.updateWidgets();
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.topicId !== this.props.topicId) {
      this.updateWidgets();
    }
  }

  async onSaveAll() {
    await this.props.saveAllWidgets(this.props.topicId, this.state.widgets);
    this.updateWidgets();
  }

  getLastElement() {
    return this.state.widgets.length - 1;
  }

  async updateWidgets() {
    const widgets = await service.findWidgetsForTopic(this.props.topicId);
    this.setState({ widgets: widgets });
  }

  async addWidget() {
    // this.props.createWidget(this.props.topicId);
    this.setState({
      widgets: [...this.state.widgets,
      { name: '', widgetType: 'Heading', id: (new Date()).getTime() + "" }]
    })
  }

  onDelete(widgetId) {
    let newWidgets = this.state.widgets.filter(widget => widget.id !== widgetId);

    this.setState({ widgets: newWidgets });

    this.setState({
      widgets: []
    })

    // this.props.deleteWidget(widgetId);
  }

  swapElements(widgets, x, y) {
    let temp = widgets[x];
    widgets[x] = widgets[y];
    widgets[y] = temp;
    widgets[x].index = x;
    widgets[y].index = y;
    return widgets;
  }

  async moveUp(widget) {
    let index = this.state.widgets.indexOf(widget);

    this.setState(
      { widgets: this.swapElements(this.state.widgets, index, index - 1) });

    // this.props.moveWidgetUp(widget, this.state.widgets[index - 1]);
  }

  async moveDown(widget) {
    let index = this.state.widgets.indexOf(widget);

    this.setState(
      { widgets: this.swapElements(this.state.widgets, index, index + 1) });

      // this.props.moveWidgetDown(widget, this.state.widgets[index + 1]);
  }


  updateWidget(newWidget) {
    this.setState({
      widgets: this.state.widgets.map(widget =>
        widget.id === newWidget.id ? newWidget : widget)
    });
  }

  // TODO: ALL THESE ARE UPDATE FUNCTIONS
  // UTILIZE UPDATE WIDGET TO UPDATE ALL OF THESE

  updateWidgetType(widget, type) {

    let newWidget = { ...widget, topicId: this.props.topicId, widgetType: type };

    this.setState({
      widgets: this.state.widgets.map(curWidget =>
        curWidget.id === widget.id ? newWidget : curWidget)
    });

    // this.props.updateWidget(newWidget);
  }

  updateWidgetName(widgetId, widget, event) {
    console.log(event.target.value);
    let newWidget = { ...widget, name: event.target.value };

    newWidget.topicId = this.props.topicId;

    this.setState({
      widgets: this.state.widgets.map(curWidget =>
        curWidget.id === widgetId ? newWidget : curWidget)
    });

    // this.props.updateWidget(newWidget);
  }

  updateWidgetContent(widgetId, widget, event) {
    let newWidget = { ...widget, content: event.target.value };
    console.log(newWidget.content);
    newWidget.topicId = this.props.topicId;
    this.setState({
      widgets: this.state.widgets.map(curWidget =>
        curWidget.id === widgetId ? newWidget : curWidget)
    });
    this.props.updateWidget(newWidget);
  }

  updateHeadingSize(widgetId, widget, event) {
    console.log(event.target.value);
    let newWidget = { ...widget, headingSize: event.target.value };
    newWidget.topicId = this.props.topicId;
    this.setState({
      widgets: this.state.widgets.map(curWidget =>
        curWidget.id === widgetId ? newWidget : curWidget)
    });

    this.props.updateWidget(widget);
  }

  // addFields(widget, index, ) {
  //   widget.topicId = this.props.topicId;
  //   widget.widgetOrder = index;
  // }

  render() {
    return (
      <span>
        <SaveAndPreviewComponent
          saveAll={this.onSaveAll.bind(this)}/>
        <div>
          {this.state.widgets && this.state.widgets.map((widget, index) =>
            <div>
              <div className={"col container fluid wbdv-widget info"}>
                <WidgetBarComponent
                  type={!widget.widgetType ? HEADING : widget.widgetType}
                  widget={widget}
                  widgetOrder={index}
                  getLastElement={this.getLastElement.bind(this)}
                  moveUp={this.moveUp.bind(this, widget)}
                  moveDown={this.moveDown.bind(this, widget)}
                  updateWidgetType={this.updateWidgetType.bind(this, widget)}
                  onDelete={this.onDelete.bind(this, widget.id)}/>

                {(!widget.widgetType || widget.widgetType === HEADING) &&
                  <HeadingWidgetComponent
                    key={widget.id}
                    widget={widget}
                    size={widget.size ? widget.size : DEFAULT_HEADING_SIZE}
                    updateWidget={this.updateWidget.bind(this, widget)}/>}

                {widget.widgetType === PARAGRAPH &&
                  <ParagraphWidgetComponent
                    key={widget.id}
                    widget={widget}
                    updateWidget={this.updateWidget.bind(this, widget)}/>}
              </div>
            </div>
          )
          }
          <div className="nav-item" onClick={() => this.addWidget()}>
            <i className="nav-link fa fa-plus-circle fa-2x" />
          </div>
        </div>
      </span>
    )
  }
}

const stateToPropertyMapper = (state) => {
  return {
    widgets: state.widgets.widgets
  }
};

const dispatchToPropertyMapper = (dispatch) => {
  return {
    createWidget: (topicId) =>
      dispatch(createWidget(topicId)),

    findAllWidgetsForTopic: (topicId) =>
      service.findWidgetsForTopic(topicId)
        .then(actualWidgets =>
          dispatch(findAllWidgetsForTopic(actualWidgets))),

    updateWidget: (widget) =>
      dispatch(updateWidget(widget)),

    deleteWidget: (widgetId) =>
      dispatch(deleteWidget(widgetId)),

    saveAllWidgets: (topicId, widgets) =>
      service.saveAllWidgets(topicId, widgets)
        .then(returnedWidgets =>
          dispatch(saveAllWidgets(returnedWidgets))),

    moveWidgetUp: (widget, other) =>
      dispatch(moveWidgetUp(widget, other)),

    moveWidgetDown: (widget, other) =>
      dispatch(moveWidgetDown(widget, other))
  }
};

export default connect(
  stateToPropertyMapper,
  dispatchToPropertyMapper)
  (WidgetListComponent)
